class Konto(object):
    __kontostand = 0
    kontonummer = 1000
    inhaber = ""

    def __init__(self, kontonummer):
        self.kontonummer = kontonummer
        print("Konto {0} initialisiert".format(kontonummer))

    def einzahlen(self, betrag):
        if betrag > 0:
            self.__kontostand = self.__kontostand + betrag
            print("Der Betrag {0} wurde eingezahlt".format(self.__kontostand))
            print("Neuer Kontostand: " + str(self.__kontostand) + "€")
        else:
            print("Der Betrag muss positiv sein.")

    def abheben(self, betrag):
        if betrag > 0:
            self.__kontostand = self.__kontostand - betrag
            print("Der Betrag {0} wurde abgehoben".format(self.__kontostand))
            print("Neuer Kontostand: " + str(self.__kontostand) + "€")
        else:
            print("Der Betrag muss positiv und größer 0 sein.")